long companyId = GetterUtil.getLong((String) workflowContext.get(WorkflowConstants.CONTEXT_COMPANY_ID));
ServiceContext serviceContext = (ServiceContext) workflowContext.get(WorkflowConstants.CONTEXT_SERVICE_CONTEXT);
Role supervisorRole = (Role) serviceContext.getAttribute("vgcapon_seatOffice_supervisor");
user = null;
roles = new ArrayList<Role>();
roles.add(supervisorRole);


import com.liferay.portal.kernel.util.GetterUtil;
import com.liferay.portal.kernel.workflow.WorkflowConstants;
import com.liferay.portal.service.ServiceContext;
import com.liferay.portal.service.RoleLocalServiceUtil;
import com.liferay.portal.model.Role;

long companyId = GetterUtil.getLong((String) workflowContext.get(WorkflowConstants.CONTEXT_COMPANY_ID));
ServiceContext serviceContext = (ServiceContext) workflowContext.get(WorkflowConstants.CONTEXT_SERVICE_CONTEXT);
Role[] adminRoles = (Role[]) serviceContext.getAttribute("vgcapon_SO_Admin_Seat_Management");
roles = new ArrayList<Role>();
for(Role role:adminRoles) {
	roles.add(role);
}
user = null;

